// 目标:
// 打造复杂形状臂旋星系

import * as THREE from 'THREE';

// 导入轨道控制器
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

import * as dat from 'dat.gui';


const gui = new dat.GUI()
// 创建场景
const scene = new THREE.Scene()

// 创建相机
const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000)
camera.position.set(0, 0, 10)
scene.add(camera)



let geometry = null
let material = null
const generateGalaxy = (params, scene) => {
  const textureLoader = new THREE.TextureLoader()

  const particlesTexture = textureLoader.load(`./textures/particles/${params.url}.png`)
  // 生成顶点
  geometry = new THREE.BufferGeometry()
  // 随机生成位置
  const positions = new Float32Array(params.count * 3)
  // 设置顶点颜色
  const colors = new Float32Array(params.count * 3)
  // 循环生成点
  for (let i = 0; i < params.count; i++) {
    const current = 3 * i
    positions[current] = Math.random() * params.radius
    positions[current + 1] = 0
    positions[current + 2] = 0
  }
  geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3))
  // 设置点的材质
  material = new THREE.PointsMaterial({
    color: new THREE.Color(params.color),
    size: params.size,
    sizeAttenuation: true,
    depthWrite: false,
    blending: THREE.AdditiveBlending,
    map: particlesTexture,
    alphaMap: particlesTexture,
    transparent: true,
    // vertexColors: true
  })
  const points = new THREE.Points(geometry, material)
  scene.add(points)
}
const params = {
  count: 100,
  size: 0.1,
  radius: 5,
  branch: 3,
  color: '#ffffff',
  url: '1'
}
generateGalaxy(params, scene)


// 初始化渲染器
const renderer = new THREE.WebGLRenderer()
// 设置渲染尺寸大小
renderer.setSize(window.innerWidth, window.innerHeight)

// 将渲染内容canvas添加到body
document.body.appendChild(renderer.domElement)

// 创建轨道控制器
const controls = new OrbitControls(camera, renderer.domElement)

// 设置控制器阻尼，让控制器更加真实，必须在动画循环里调用update
controls.enableDamping = true

// 添加坐标轴辅助器
const axesHelper = new THREE.AxesHelper(5)
scene.add(axesHelper)

let clock = new THREE.Clock()

// 设置渲染函数
function render () {
  let time = clock.getElapsedTime()
  controls.update()
  // 使用渲染器通过相机将场景渲染出来
  renderer.render(scene, camera)

  // 下一帧继续render
  requestAnimationFrame(render)

}
render()
